mapper.select:
SELECT
FROM rewproject p
left join rewrecomidea a on a.projectId = p.projectId left join (select a.projectId as "projectId", ROUND((avg(a.KPIWeight1) + AVG(a.KPIWeight2) + AVG(a.KPIWeight3) + AVG(a.KPIWeight4) + AVG(a.KPIWeight5)),2) as "scope" from rewprojectscope a group by a.projectId )t on t.projectId = p.projectId WHERE p.projectId = #{projectid} AND p.currentYear = #{currentYear}

SELECT
,g.groupName as "groupName" ,c.categoryName as "categoryName" FROM rewproject p left join rewrecomidea a on a.projectId = p.projectId left join rewgroup g on g.id = p.groupId left join rewcategory c on c.id = p.categoryId
left join (select a.projectId as "projectId", ROUND((avg(a.KPIWeight1) + AVG(a.KPIWeight2) + AVG(a.KPIWeight3) + AVG(a.KPIWeight4) + AVG(a.KPIWeight5)),2) as "scope" from rewprojectscope a group by a.projectId )t on t.projectId = p.projectId
p.projectId = #{projectid}
and p.groupId = #{currentUser.groupId}
and p.currentYear = #{currentYear}
ORDER BY ${page.orderBy}
ORDER BY p.groupId,t.scope DESC

SELECT a.id AS "id", p.projectid AS "projectid", p.currentYear as "currentYear", IFNULL(t.scope,0) AS "scope", a.recomidea AS "recomidea", a.create_by AS "createBy.id", a.create_date AS "createDate", a.update_by AS "updateBy.id", a.update_date AS "updateDate" ,g.groupName as "groupName" ,g.id as "groupId" ,c.categoryName as "categoryName" ,"未通过" as "result" ,"0" as num FROM rewproject p left join rewrecomidea a on a.projectId = p.projectId left join rewgroup g on g.id = p.groupId left join rewcategory c on c.id = p.categoryId
left join (select a.projectId as "projectId", ROUND((avg(a.KPIWeight1) + AVG(a.KPIWeight2) + AVG(a.KPIWeight3) + AVG(a.KPIWeight4) + AVG(a.KPIWeight5)),2) as "scope" from rewprojectscope a group by a.projectId )t on t.projectId = p.projectId
p.groupId = #{currentUser.groupId}
and p.currentYear = #{currentYear}
and p.groupId = #{groupId}
ORDER BY ${page.orderBy}
ORDER BY p.groupId,t.scope DESC

select a.id AS "id", p.projectid AS "projectid", a.recomidea AS "recomidea", a.create_by AS "createBy.id", a.create_date AS "createDate", a.update_by AS "updateBy.id", a.update_date AS "updateDate", a.allNum as "allNum", a.num as "num", a.isOK as "result" from rewproject p left join rewrecomidea a on a.projectId = p.projectId
p.groupId=#{groupId}
and p.currentYear = #{currentYear}
